import { ActionTypes } from "../constants/action-types"

const initialState = {
    product : [
        {
            id:1,
            title:"aftab",
            category:"developer",
        }
    ]
}

const producer = (state = initialState,{ type,payload })=>{
    console.log(ActionTypes.SET_PRODUCT);
    switch(type){
        
        case ActionTypes.SET_PRODUCT :
            return state;
        default:
            return state;
    }
}

export default producer;
